// Write all the code here
import React, { useState } from 'react';
import Popup from 'reactjs-popup';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const MyPopup = () => {
  const [message, setMessage] = useState('');

  const handleInputChange = (event) => {
    setMessage(event.target.value);
  };

  const handleClose = () => {
    // Perform any additional actions before closing the popup
    Popup.close();
  };

  const handleSend = () => {
    // Handle the send action, e.g., send the message to a server
    console.log('Message sent:', message);
    // Close the popup
    Popup.close();
  };

  return (
    <Popup
      trigger={<button type="button" className="btn btn-primary">Send</button>}
      position="right center"
      modal
    >
      <head>
     <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" integrity="..." crossorigin="anonymous"></link>
      </head>
      <div className="container">
        <div className="form-group">
          <label htmlFor="messageBox">Insert your message here:</label>
          <textarea
            id="messageBox"
            className="form-control"
            rows="4"
            value={message}
            onChange={handleInputChange}
          />
        </div>
        <button className="btn btn-secondary" onClick={handleClose}>
          Close
        </button>
        <button className="btn btn-primary ml-2" onClick={handleSend}>
          Send
        </button>
      </div>
    </Popup>
  );
};

export default MyPopup;
